function toggleText(e){e=e.closest(".about-me").querySelector(".about-text");e.classList.contains("hidden")?e.classList.remove("hidden"):e.classList.add("hidden")}window.addEventListener("load",()=>{document.querySelectorAll(".progress").forEach(e=>{var t=e.getAttribute("data-progress");e.style.width=t+"%"})}),document.addEventListener("DOMContentLoaded",function(){var e=document.getElementById("skills-toggle");let t=document.getElementById("skills-text");document.querySelectorAll(".progress");e.addEventListener("click",function(){t.classList.toggle("expanded")})});